template('hello','<!doctype html> <html> <head> <meta charset="UTF-8"> <title>Hello</title> <script type="text/javascript" src="/javascripts/common/jquery-3.1.1.min.js"></script> <script type="text/javascript" src="/javascripts/common/echarts.min.js"></script> </head> <body> <div class="description">Hello</div> <div id="main" style="width: 300px;height:400px;"></div> <script type="text/javascript"> var myChart = echarts.init(document.getElementById(\'main\')); // 显示标题，图例和空的坐标轴 myChart.setOption({ title: { text: \'访问记录的折线图\' }, tooltip: { trigger: \'axis\' }, /* legend: { data:[\'订单量\'] },*/ xAxis: { data: [] }, yAxis: {}, series: [{ name: \'访问量\', type: \'line\', data: [] }] }); myChart.showLoading();//展示加载动画 // 异步加载数据 var requestUrl = "/data/data=0101/type=left"; $.post(requestUrl,function(data){ console.log(data); }); $.get(\'data\').done(function (data) { myChart.hideLoading();//隐藏加载动画 // 填入数据 var timeList = []; $.each(data,function(item){ timeList.push(data[item].timeT); }); var countList = []; $.each(data,function(item){ countList.push(data[item].Count); }) myChart.setOption({ xAxis: { data:timeList }, yAxis: { splitLine: { show: false } }, dataZoom: [{ startValue: \'2017-01-01 00:00\' }, { type: \'inside\' }], series: [{ // 根据名字对应到相应的系列 name: \'访问量\', data: countList }] }); }); </script> </body> </html> ');